/**
 * Installer base class.
 */
class Classifieds_Base_Installer extends Zikula_AbstractInstaller
{
    /**
     * Install the Classifieds application.
     *
     * @return boolean True on success, or false.
     */
    public function install()
    {
        // Check if upload directories exist and if needed create them
        try {
            $controllerHelper = new Classifieds_Util_Controller($this->serviceManager);
            $controllerHelper->checkAndCreateAllUploadFolders();
        } catch (\Exception $e) {
            return LogUtil::registerError($e->getMessage());
        }
        // create all tables from according entity definitions
        try {
            DoctrineHelper::createSchema($this->entityManager, $this->listEntityClasses());
        } catch (\Exception $e) {
            if (System::isDevelopmentMode()) {
                LogUtil::registerError($this->__('Doctrine Exception: ') . $e->getMessage());
            }
            $returnMessage = $this->__f('An error was encountered while creating the tables for the %s extension.', array($this->name));
            if (!System::isDevelopmentMode()) {
                $returnMessage .= ' ' . $this->__('Please enable the development mode by editing the /config/config.php file in order to reveal the error details.');
            }
            return LogUtil::registerError($returnMessage);
        }
    
        // set up all our vars with initial values
        $this->setVar('pageSize', 20);
        $this->setVar('defaultPeriod', 60);
        $this->setVar('termsLink', '/index.php');
        $this->setVar('pictureFileSize', 204800);
        $this->setVar('pictureHeight', 200);
        $this->setVar('pictureWidth', 200);
        $this->setVar('thumbPictureHeight', 64);
        $this->setVar('thumbPictureWidth', 64);
        $this->setVar('pictureDummy', 'modules/Classifieds/images/dummy.png');
        $this->setVar('useWatermark', false);
        $this->setVar('typWatermark',  'text' );
        $this->setVar('watermarkPicture', 'modules/Classifieds/images/watermark.png');
        $this->setVar('textWatermark', 'copyright');
    
        $categoryRegistryIdsPerEntity = array();
    
        // add default entry for category registry (property named Main)
        include_once 'modules/Classifieds/lib/Classifieds/Api/Base/Category.php';
        include_once 'modules/Classifieds/lib/Classifieds/Api/Category.php';
        $categoryApi = new Classifieds_Api_Category($this->serviceManager);
    
        $registryData = array();
        $registryData['modname'] = $this->name;
        $registryData['table'] = 'Classified';
        $registryData['property'] = $categoryApi->getPrimaryProperty(array('ot' => 'Classified'));
        $categoryGlobal = CategoryUtil::getCategoryByPath('/__SYSTEM__/Modules/Global');
        $registryData['category_id'] = $categoryGlobal['id'];
        $registryData['id'] = false;
        if (!DBUtil::insertObject($registryData, 'categories_registry')) {
            LogUtil::registerError($this->__f('Error! Could not create a category registry for the %s entity.', array('classified')));
        }
        $categoryRegistryIdsPerEntity['classified'] = $registryData['id'];
    
        // create the default data
        $this->createDefaultData($categoryRegistryIdsPerEntity);
    
        // register persistent event handlers
        $this->registerPersistentEventHandlers();
    
        // register hook subscriber bundles
        HookUtil::registerSubscriberBundles($this->version->getHookSubscriberBundles());
        
    
        // initialisation successful
        return true;
    }
    
